function copyToClipboard(e){const t=document.getElementById(e);if(!t)return;const n=t.textContent.replace(/^\$\s/gm,"");navigator.clipboard.writeText(n).then(()=>{const t=document.querySelector(`button[onclick="copyToClipboard('${e}')"]`);if(t){const n=t.nextElementSibling;n&&n.classList.contains("copied-message")&&n.remove();let e=document.createElement("span");e.textContent="Copied!",e.classList.add("copied-message"),t.parentNode.insertBefore(e,t.nextSibling),setTimeout(()=>{e.remove()},2e3)}}).catch(e=>{console.error("Failed to copy: ",e)})}